select TRUNC("entry_timestamp", 'hour') as event_hour, "account_id",  "media":id as media_id, "device":type as device_type, "device":osName as device_os, "domain", sum("seconds_played") as seconds_played, count_if("media":event = 'video_view') as video_views from player group by event_hour, "account_id", "media":id, "device":type, "device":osName, "domain"
